div.script:
function updateDateTime() {
  var now = new Date();
  var year = now.getUTCFullYear();
  var month = (now.getUTCMonth() + 1).toString().padStart(2, '0');
  var day = now.getUTCDate().toString().padStart(2, '0');
  var hours = now.getUTCHours().toString().padStart(2, '0');
  var minutes = now.getUTCMinutes().toString().padStart(2, '0');
  var seconds = now.getUTCSeconds().toString().padStart(2, '0');

  document.getElementById('current-datetime').innerHTML = 
    year + '-' + month + '-' + day + ' ' + hours + ':' + minutes + ':' + seconds;

  setTimeout(updateDateTime, 1000);
}
document.addEventListener('DOMContentLoaded', updateDateTime);

body.script:
      // Basic chat functionality
      document.addEventListener('DOMContentLoaded', function() {
        const chatInput = document.querySelector('.chat-input');
        const sendButton = document.querySelector('.chat-send-btn');
        const chatHistory = document.querySelector('.chat-history');

        function sendMessage() {
          const message = chatInput.value.trim();
          if (message) {
            // Create message element
            const now = new Date();
            const messageEl = document.createElement('div');
            messageEl.className = 'chat-message';
            messageEl.innerHTML = `

A



${message}

just now

            `;

            // Add before the end of chat history
            chatHistory.appendChild(messageEl);

            // Clear input
            chatInput.value = '';

            // Scroll to bottom
            chatHistory.scrollTop = chatHistory.scrollHeight;
          }
        }

        // Send on button click
        sendButton.addEventListener('click', sendMessage);

        // Send on Enter key
        chatInput.addEventListener('keypress', function(e) {
          if (e.key === 'Enter') {
            sendMessage();
            e.preventDefault();
          }
        });
      });
